import React, { useState } from "react";
import classes from "./Users.module.css";

const DUMMY_USERS = [
  { id: "u1", name: "Ama" },
  { id: "u2", name: "Abena" },
  { id: "u3", name: "Kwame" },
  { id: "u4", name: "Michael" },
];

const Users = () => {
  const [showUsers, setShowUsers] = useState(true);
  const [filterUsers, setFilterUsers] = useState("");

  const filterHandler = (event) => {
    setFilterUsers(event.target.value);
  };

  const search = !filterUsers
    ? DUMMY_USERS
    : DUMMY_USERS.filter((person) =>
        person.name.toLowerCase().includes(filterUsers.toLowerCase())
      );

  const toggleHandler = () => {
    setShowUsers((curState) => !curState);
  };

  const userList = (
    <ul>
      {DUMMY_USERS.map((user) => (
        <li key={user.id}>{user.name}</li>
      ))}
    </ul>
  );

  const showFilter = () => {
    search.map((person) => {
      <p key={person.id}> {person.name}</p>;
    });
  };

  return (
    <fragment className={classes["User-List"]}>
      <div>
        <input type="search" value={filterUsers} onChange={filterHandler} />
      </div>
      <div>
        <button className={classes.btn} onClick={toggleHandler}>
          {showUsers ? "Hide" : "show"} Users
        </button>
      </div>
      {showUsers && userList}
      {showFilter}
    </fragment>
  );
};

export default Users;
