import React, { useState } from "react";
import classes from "./Users.module.css";

const DUMMY_USERS = [
  { id: "u1", name: "Ama" },
  { id: "u2", name: "Abena" },
  { id: "u3", name: "Kwame" },
  { id: "u4", name: "Michael" },
];

const Users = () => {
  const [showUsers, setShowUsers] = useState(true);
  const [filterUsers, setFilterUsers] = useState("");
  const [addUser, setAddUser] = useState("");

  // console.log(addUser);

  const filterHandler = (event) => {
    setFilterUsers(event.target.value);
    setAddUser(event.target.value);
  };

  const addUserHandler = () => {
    if (addUser.trim().length === 0) {
      return;
    } else {
      setAddUser((prevAddUser) => {
        return [...prevAddUser];
      });
    }
  };

  const search = !filterUsers
    ? DUMMY_USERS
    : DUMMY_USERS.filter((user) =>
        user.name.toLowerCase().includes(filterUsers.toLowerCase())
      );

  const toggleHandler = () => {
    setShowUsers((curState) => !curState);
  };

  const userList = (
    <ul>
      {search.map((user) => (
        <li key={user.id}>{user.name}</li>
      ))}
    </ul>
  );

  return (
    <div className={classes["User-List"]}>
      <div>
        <input type="text" value={filterUsers} onChange={filterHandler} />
      </div>
      <div>
        <button className={classes.btn} onClick={toggleHandler}>
          {showUsers ? "Hide" : "show"} Users
        </button>
        <button onSubmit={addUserHandler}>Add Person</button>
      </div>

      {showUsers && userList}
    </div>
  );
};

export default Users;
